// Setze Sample-Nummer für nachfolgende Verarbeitung
const sample = $json.query?.sample || '';
return {
  json: {
    sample: sample,
    source: sample ? `Sample ${sample}` : 'Upload'
  },
  binary: $binary
};

// Konvertiere Binary zu Base64 für Vertex AI
// Prüfe ob Binary-Daten vorhanden sind
// In n8n: Binary-Feld heißt 'file' (wie im Webhook definiert)

let binaryData = null;
let mimeType = 'image/jpeg';

// Versuche verschiedene Möglichkeiten für Binary-Daten-Zugriff
if ($binary && $binary.file) {
  // Standard: $binary.file (wenn binaryPropertyName='file')
  binaryData = $binary.file.data;
  mimeType = $binary.file.mimeType || 'image/jpeg';
} else if ($binary && $binary.data) {
  // Fallback: $binary.data
  binaryData = $binary.data.data || $binary.data;
  mimeType = $binary.data.mimeType || 'image/jpeg';
} else if ($binary && typeof $binary === 'object') {
  // Fallback: Erstes Binary-Feld finden
  const binaryKeys = Object.keys($binary);
  if (binaryKeys.length > 0) {
    const firstKey = binaryKeys[0];
    binaryData = $binary[firstKey].data;
    mimeType = $binary[firstKey].mimeType || 'image/jpeg';
  }
}

// Prüfe ob Binary-Daten vorhanden sind
if (!binaryData) {
  throw new Error('Kein Binary-Daten gefunden! Stelle sicher, dass ein Bild hochgeladen wurde oder ein Sample geladen wurde. Prüfe auch ob der Webhook binaryPropertyName=\"file\" korrekt gesetzt ist.');
}

// Base64 Encoding
// Buffer sollte in n8n verfügbar sein
let base64;
try {
  // Versuche Buffer (Node.js)
  if (typeof Buffer !== 'undefined') {
    base64 = Buffer.from(binaryData, 'binary').toString('base64');
  } else {
    // Fallback: Binary-Daten sind möglicherweise schon Base64
    if (typeof binaryData === 'string') {
      // Wenn es schon Base64 ist, nimm es direkt
      base64 = binaryData.replace(/^data:.*?;base64,/, '');
    } else {
      // Konvertiere Array/Uint8Array zu Base64
      const bytes = new Uint8Array(binaryData);
      const binString = Array.from(bytes, byte => String.fromCharCode(byte)).join('');
      base64 = btoa(binString);
    }
  }
} catch (error) {
  console.error('Fehler bei Base64-Konvertierung:', error);
  throw new Error(`Base64-Konvertierung fehlgeschlagen: ${error.message}`);
}

// Validiere Base64
if (!base64 || base64.length === 0) {
  throw new Error('Base64-Konvertierung hat leeren String erzeugt!');
}

return {
  json: {
    sample: $json.sample || '',
    source: $json.source || 'Upload',
    imageBase64: base64,
    mimeType: mimeType
  }
};

// Parse AI Agent Response (Vertex AI Gemini)
const response = $json;
const source = $('Setze Sample-Info').item.json.source || 'Upload';
const sample = $('Setze Sample-Info').item.json.sample || '';

// AI Agent Response Struktur kann variieren:
// Option 1: Direkt JSON Response
// Option 2: Text mit JSON
// Option 3: Strukturierte Response mit output/chatOutput

let extractedData = {};
let responseText = '';

try {
  // Versuche verschiedene Response-Strukturen
  if (response.output) {
    responseText = response.output;
  } else if (response.chatOutput) {
    responseText = response.chatOutput;
  } else if (response.text) {
    responseText = response.text;
  } else if (response.message) {
    responseText = response.message;
  } else if (response.candidates?.[0]?.content?.parts?.[0]?.text) {
    // Vertex AI native Format
    responseText = response.candidates[0].content.parts[0].text;
  } else if (typeof response === 'string') {
    responseText = response;
  } else if (typeof response === 'object' && response.name) {
    // Bereits strukturiertes JSON
    extractedData = response;
  }

  // Extrahiere JSON aus Text (falls zusätzlicher Text vorhanden)
  if (responseText) {
    const jsonMatch = responseText.match(/\{[\s\S]*\}/);
    const jsonString = jsonMatch ? jsonMatch[0] : responseText;
    extractedData = JSON.parse(jsonString);
  }
} catch (error) {
  // Fallback: Versuche direkt zu parsen
  if (typeof response === 'object' && !response.candidates) {
    extractedData = response;
  } else {
    console.error('Fehler beim Parsen der AI Agent Response:', error);
    console.error('Response:', JSON.stringify(response, null, 2));
    extractedData = {};
  }
}

// Füge Metadaten hinzu
return [{
  json: {
    name: extractedData.name || '',
    company: extractedData.company || '',
    email: extractedData.email || '',
    phone: extractedData.phone || extractedData.phone_mobile || '',
    phone_office: extractedData.phone_office || extractedData.phone_büro || '',
    address: extractedData.address || '',
    city: extractedData.city || '',
    website: extractedData.website || extractedData.www || '',
    job_title: extractedData.job_title || extractedData.position || '',
    source: source,
    sample: sample,
    timestamp: new Date().toISOString()
  }
}];

// Formatiere Daten für die Website
const data = $json;
return {
  json: {
    type: 'business-card-processed',
    payload: {
      name: data.name || '',
      company: data.company || '',
      email: data.email || '',
      phone: data.phone || '',
      phone_office: data.phone_office || '',
      address: data.address || '',
      city: data.city || '',
      website: data.website || '',
      job_title: data.job_title || '',
      source: data.source || 'Upload',
      sample: data.sample || '',
      timestamp: data.timestamp || new Date().toISOString()
    }
  }
};
