// Parse AI Agent Response (Vertex AI Gemini)

// Detailliertes Logging
console.log('üîç [Parse AI Agent Response] Starte Parsing');
console.log('‚è∞ Zeitstempel:', new Date().toISOString());
console.log('üìã Input Response:', JSON.stringify($json, null, 2));

const response = $json;
const source = $('Setze Sample-Info').item.json.source || 'Upload';
const sample = $('Setze Sample-Info').item.json.sample || '';

console.log('üìù Metadaten:', { source, sample });

// AI Agent Response Struktur kann variieren:
// Option 1: Direkt JSON Response
// Option 2: Text mit JSON

let extractedData = {};
let responseText = '';

try {
  // Versuche verschiedene Response-Strukturen
  if (response.output) {
    responseText = response.output;
  } else if (response.chatOutput) {
    responseText = response.chatOutput;
  } else if (response.text) {
    responseText = response.text;
  } else if (response.message) {
    responseText = response.message;
  } else if (response.candidates?.[0]?.content?.parts?.[0]?.text) {
    // Vertex AI native Format
    responseText = response.candidates[0].content.parts[0].text;
  } else if (typeof response === 'string') {
    responseText = response;
  } else if (typeof response === 'object' && response.name) {
    // Bereits strukturiertes JSON
    extractedData = response;
  }

  // Extrahiere JSON aus Text (falls zus√§tzlicher Text vorhanden)
  if (responseText) {
    const jsonMatch = responseText.match(/\{[\s\S]*\}/);
    const jsonString = jsonMatch ? jsonMatch[0] : responseText;
    extractedData = JSON.parse(jsonString);
  }
} catch (error) {
  // Fallback: Versuche direkt zu parsen
  if (typeof response === 'object' && !response.candidates) {
    extractedData = response;
  } else {
    console.error('Fehler beim Parsen der AI Agent Response:', error);
    console.error('Response:', JSON.stringify(response, null, 2));
    extractedData = {};
  }
}

